	this._Layer.visible			=	false;
	
	// sand foreground
		SetBlend					( 7 );
		SetAlpha					( 0.4 );
		SetColor					( 220, 150, 080 );
		this._Orange			=	AddRect( 0,0,l_W, l_H, this._Layer, 4 );
		SetBlend					( 0 );
		
	// cinema bands
		SetAlpha					( 1.0 );
		SetColor					( 000, 000, 000 );
		SetHandle(0,0);
		this._TopBar			=	AddRect( 0,0,l_W, 80, this._Layer, 4 );
		
		SetHandle(0,1);
		this._BotBar			=	AddRect( 0,l_H,l_W, -80, this._Layer, 4 );
		
	// animate front layer -> to make it transparent (we don't want the cinematic be hidden by a tree)
	var l_Shape					=	new TAnimShape (TGame.layers[TLayer.FRONT], true); this._Shapes.addLast(l_Shape);
		l_Shape.addAnimator			( 'alpha'	, 0, 500, TGame.layers[TLayer.FRONT].alpha, 0.35 );
		l_Shape.addAnimator			( 'alpha'	, this._Length-750, 500, 0.35, TGame.layers[TLayer.FRONT].alpha );
		
	// enable the main layer
	l_Shape						=	new TAnimShape (this._Layer, false); this._Shapes.addLast(l_Shape);
		l_Shape.addAnimator			( 'enable'	,                1,   1 );
		l_Shape.addAnimator			( 'alpha'	,                1, 500, 0, 1 );
		l_Shape.addAnimator			( 'alpha'	, l_Len-300, 250, 1, 0 );
		l_Shape.addAnimator			( 'disable'	, l_Len- 50,   1 );
		
	// animate cinema bands.
	l_Shape						=	new TAnimShape (this._TopBar, false); this._Shapes.addLast(l_Shape);
		l_Shape.addAnimator			( 'scale-y'	,     500, 250, 0.001,1 );
		l_Shape.addAnimator			( 'enable'	,     501,   1 );
		l_Shape.addAnimator			( 'disable'	, l_Len-1,   1 );
		
	l_Shape						=	new TAnimShape (this._BotBar, false); this._Shapes.addLast(l_Shape);
		l_Shape.addAnimator			( 'scale-y'	,      500, 250, 0.001,1 );
		l_Shape.addAnimator			( 'enable'	,      501,   1 );
		l_Shape.addAnimator			( 'disable'	, l_Len- 1,   1 );
	
	
	// animation steps
	TIntroSeqID.Reset();
	
	this.onInit = function(){};
	this.onStart = function(){};
	this.onUpdate = function(pRate){}
	this.onStop = function(){}
	
	this._start = function(){
		this.onStart();
		var l_Link = this._Shapes.firstLink();
		while (l_Link!=null){
			var l_Shape = l_Link.value();
			l_Link = l_Link.after();
			l_Shape.animate(true);
		}
	};
	this._update = function (pRate){
		this.onUpdate(pRate);
		var t = MilliSecs();
		var l_Link = this._Shapes.firstLink();
		while (l_Link!=null){
			var l_Shape = l_Link.value();
			l_Link = l_Link.after();
			l_Shape.update(t);
		}
	}
	this._stop = function(){
		this.onStop();
		
		var l_Link = this._Shapes.firstLink();
		while (l_Link!=null){
			var l_Anm = l_Link.value();
			l_Anm.destroy();
			l_Link = l_Link.after();
		}
		this._Shapes.clear();
		this._Layer.destroy();
		this._Layer = null;
	}
	
	
	this._Steps = {
		
		ID:TIntroSeqID(),
		
		start:function(pCtx) {
			// disable UI -> full cinema mode
			this._FxVisible = TGame.layers[TLayer.POSTFX].visible;
			this._UIVisible = TGame.layers[TLayer.UI].visible;
			TGame.layers[TLayer.POSTFX].visible = false;
			TGame.layers[TLayer.UI].visible = false;
			pCtx._start();
		},
		
		update:function(pRate, pCtx){
			pCtx._update(pRate);
		},
		
		stop:function(pCtx){
			// restore UI -> if it was enabled
			TGame.layers[TLayer.POSTFX].visible = this._FxVisible;
			TGame.layers[TLayer.UI].visible = this._UIVisible;
			pCtx._stop();
		}
	};
	
	this.onInit();
	
	// create a sequencer to "animate"
	this._Sequencer			=	new TSequencer(pName, this);
	
	// add steps of intro
	this._Sequencer				.add(this._Steps, this._Length);
	
	return this;
