function TSeqEnableSprite(pSprite, pAlpha=1){
	pSprite.alpha	=	pAlpha;
	pSprite.visible	=	true;
	pSprite.smoothed=	false;
}

function TSeqDisableSprite(pSprite, pAlpha=1){
	pSprite.alpha	=	pAlpha;
	pSprite.visible	=	false;
}

function TSeqLerpAlpha( pSprite, pRate, pValue0, pValue1 ){
	pSprite.alpha	=	Clamp(pValue0+(pValue1-pValue0)*pRate,pValue0,pValue1);
}

function TSeqInvLerpAlpha( pSprite, pRate, pValue0, pValue1 ){
	pSprite.alpha	=	Clamp(pValue1+(pValue0-pValue1)*pRate,pValue0,pValue1);
}

function TilePosition(pSprite, pX,pY){
	pSprite.cameraOffset.setTo(pX,pY);
}

