var _TextRGB = function (pRGB) {
	var l_r = ((pRGB & 0xff0000) >>> 16);
	var l_g = ((pRGB & 0x00ff00) >>> 8);
	var l_b = ((pRGB & 0x0000ff));
	return 'rgb(' + l_r + ',' + l_g + ',' + l_b + ')';
}

var TGadgetScheme = function(){
								// normal -  over  -   down
	this.Button_FontSize	=	[      16,       17,       18];
	this.Button_FillColor	=	[0xfff0c0, 0xc0e0ff, 0xc0a090];
	this.Button_StrokeColor	=	[0x000000, 0x500000, 0x000050];
	this.Button_StrokeSize	=	[       2,        3,        4];
	G_CURRENT_SCHEME		=	this;
	return this;
}

var G_GADGET_SCHEME			=	new TGadgetScheme();
var G_CURRENT_SCHEME		=	G_GADGET_SCHEME;
function SetScheme (pScheme){
	G_CURRENT_SCHEME		=	pScheme;
}
function SchemeFillColor	( pMode, pR,pG,pB )	{
	G_CURRENT_SCHEME.Button_FillColor[pMode]	=	(pR << 16) | (pG << 8) | pB;
}
function SchemeStrokeColor	( pMode, pR,pG,pB )	{
	G_CURRENT_SCHEME.Button_StrokeColor[pMode]=	(pR << 16) | (pG << 8) | pB;
}
function SchemeStrokeSize	( pMode, pSize )	{
	G_CURRENT_SCHEME.Button_StrokeSize[pMode]	=	pSize;
}
function SchemeFontSize		( pMode, pSize )	{
	G_CURRENT_SCHEME.Button_FontSize[pMode]	=	pSize;
}
function SchemeFillColors	( pColors )			{
	G_CURRENT_SCHEME.Button_FillColor = pColors;
}
function SchemeStrokeColors	( pColors )			{
	G_CURRENT_SCHEME.Button_StrokeColor = pColors;
}
function SchemeStrokeSizes	( pSizes )			{
	G_CURRENT_SCHEME.Button_StrokeSize = pSizes;
}
function SchemeFontSizes	( pSizes )			{
	G_CURRENT_SCHEME.Button_FontSize = pSizes;
}

function SchemeDefine		( pFontColors, pFontSizes, pStrokeColors, pStrokeSizes ){
	G_CURRENT_SCHEME.Button_FillColor	=	pFontColors;
	G_CURRENT_SCHEME.Button_FontSize	=	pFontSizes;
	G_CURRENT_SCHEME.Button_StrokeColor	=	pStrokeColors;
	G_CURRENT_SCHEME.Button_StrokeSize	=	pStrokeSizes;
}

function _UpdateButton(pButton, pMode){
	var l_Scheme			=	pButton._Scheme;
	if (l_Scheme==undefined)	{l_Scheme=G_CURRENT_SCHEME;console.log("use default");}
	
	pButton.tint			=	l_Scheme.Button_FillColor[pMode];
	pButton.fillColor		=	_TextRGB(l_Scheme.Button_FillColor[pMode]);
	pButton.strokeColor		=	_TextRGB(l_Scheme.Button_StrokeColor[pMode]);
	pButton.strokeThickness	=	l_Scheme.Button_StrokeSize[pMode];
	pButton.fontSize		=	l_Scheme.Button_FontSize[pMode];
	if (pButton._Class == GADGET_TILEBUTTON){
		pButton._TileOff.visible = (pButton._State==0);
		pButton._TileOn.visible = (pButton._State==1);
	}
}

function _Callback_BUTTON_DOWN(){
	var l_Shape = arguments[0];
	switch(l_Shape._Style){
		case BUTTON_PUSH :
			l_Shape._State = 1;
			break;
		case BUTTON_RADIO :
			l_Shape._State = !l_Shape._State;
			break;
	}
	_UpdateButton(l_Shape, 2);
	CreateEvent(EVENT_GADGETACTION, l_Shape.x,l_Shape.y, l_Shape).emit();
	if (l_Shape._CallBacks!=null && l_Shape._CallBacks.length>=2){
		if (l_Shape._CallBacks[1]!=null) l_Shape._CallBacks[1](l_Shape);
	}
}

function _Callback_BUTTON_UP(){
	var l_Shape = arguments[0];
	switch(l_Shape._Style){
		case BUTTON_PUSH :
			l_Shape._State = 0;
			break;
		case BUTTON_RADIO :
			break;
	}
	_UpdateButton(l_Shape, 1);
	CreateEvent(EVENT_GADGETUP, l_Shape.x,l_Shape.y, l_Shape, arguments).emit();
	if (l_Shape._CallBacks!=null && l_Shape._CallBacks.length>=1){
		if (l_Shape._CallBacks[0]!=null) l_Shape._CallBacks[0](l_Shape);
	}
}

function _Callback_BUTTON_LEAVE(){
	var l_Shape = arguments[0];
	_UpdateButton(l_Shape, 0);
	CreateEvent(EVENT_GADGETLEAVE, l_Shape.x,l_Shape.y, l_Shape, arguments).emit();
	if (l_Shape._CallBacks!=null && l_Shape._CallBacks.length>=4){
		if (l_Shape._CallBacks[3]!=null) l_Shape._CallBacks[3](l_Shape);
	}
}

function _Callback_BUTTON_OVER(){
	var l_Shape = arguments[0];
	_UpdateButton(l_Shape, 1);
	CreateEvent(EVENT_GADGETENTER, l_Shape.x,l_Shape.y, l_Shape, arguments).emit();
	if (l_Shape._CallBacks!=null && l_Shape._CallBacks.length>=3){
		if (l_Shape._CallBacks[2]!=null) l_Shape._CallBacks[2](l_Shape);
	}
}


