	var TParticleSpread = function(pTemplateId, pX, pY, pFreq, pDirX=0,pDirY=0, pEnabled=true) {
		this._Template	=	pTemplateId;
		this._X			=	pX;
		this._Y			=	pY;
		this._Freq		=	pFreq;
		this._DirX		=	pDirX;
		this._DirY		=	pDirY;
		this._Time		=	0;
		this._Enabled	=	pEnabled;
		return this;
	}
	
	TParticleSpread.prototype.update = function(pEmitter, pCoef, pDT){
		if (this._Enabled==false){return;}
		this._Time		-=	pDT;
		while (this._Time<=0)
		{
			this._Time	+=	this._Freq;
			if (this._Template.length>0){
			pEmitter.Emit(this._X,this._Y, this._Template[Rand(0,this._Template.length-1)], this._DirX, this._DirY);
			}else{
			pEmitter.Emit(this._X,this._Y, this._Template, this._DirX, this._DirY);
			}
		}
	}
	TParticleSpread.prototype.pause = function (){
		this._Enabled	=	false;
	}
	TParticleSpread.prototype.resume = function (){
		this._Enabled	=	true;
	}
	
	Object.defineProperty(TParticleSpread.prototype, 'enable', {
		get:function(){return this._Enabled;},
		set:function(pEnable){this._Enabled=pEnabled;}
	})
	
	
	
	var TParticle = function (pEmitterParts, pX,pY, pTemplate, pDirX=undefined,pDirY=undefined){
		
		var l_Dx	=	(pDirX!=undefined?pDirX:1);
		var l_Dy	=	(pDirY!=undefined?pDirY:1);
		
		this._X		=	Rnd(pTemplate._Params[0],pTemplate._Params[1])+pX;
		this._Y		=	Rnd(pTemplate._Params[2],pTemplate._Params[3])+pY;
		this._A		=	Rand(pTemplate._Params[4],pTemplate._Params[5]);
		this._MaxLife=	Rand(pTemplate._Params[6],pTemplate._Params[7]);
		
		this._Width	=	[Rnd(pTemplate._Params[8],pTemplate._Params[9]),
						 Rnd(pTemplate._Params[10],pTemplate._Params[11])];
		this._Height=	[Rnd(pTemplate._Params[12],pTemplate._Params[13]),
						 Rnd(pTemplate._Params[14],pTemplate._Params[15])];
		this._VelX	=	[l_Dx*Rnd(pTemplate._Params[16],pTemplate._Params[17]),
						 l_Dx*Rnd(pTemplate._Params[18],pTemplate._Params[19])];
		this._VelY	=	[l_Dy*Rnd(pTemplate._Params[20],pTemplate._Params[21]),
						 l_Dy*Rnd(pTemplate._Params[22],pTemplate._Params[23])];
		this._VelA	=	[Rnd(pTemplate._Params[24],pTemplate._Params[25]),
						 Rnd(pTemplate._Params[26],pTemplate._Params[27])];
		this._Gravity=	[Rnd(pTemplate._Params[28],pTemplate._Params[29]),
						 Rnd(pTemplate._Params[30],pTemplate._Params[31])];
		this._Alpha=	[Rnd(pTemplate._Params[32],pTemplate._Params[33]),
						 Rnd(pTemplate._Params[34],pTemplate._Params[35])];
		
		this._Life	=	0;
		
		this._W		=	this._Width[0];
		this._H		=	this._Height[0];
		this._I		=	this._VelX[0];
		this._J		=	this._VelY[0];
		this._K		=	this._VelA[0];
		this._G		=	this._Gravity[0];
		this._ClampY=	pTemplate._ClampY;
		this._ClampX=	pTemplate._ClampX;
		this._Wrap	=	pTemplate._Wrap;
		
		var l_Shape	=	TGame._Game.add.tileSprite ( this._X,this._Y, this._W, this._H, pTemplate._AtlasKey, pTemplate._AtlasFrame );
		l_Shape.tint=	0xffffff;
		l_Shape.blendMode=	0;
		l_Shape.anchor	.setTo	( 0.5,0.5 );
		l_Shape.scale	.setTo	( 1,1 );
		l_Shape.angle=	this._A;
		l_Shape.fillAlpha=	this._Alpha;
		pTemplate._Layer.add(l_Shape);
		
		if (pTemplate._Flag>0){ if ((4 & pTemplate._Flag) > 0){ l_Shape.fixedToCamera = true; } }
		
		this._Sprite=	l_Shape;
		this._Link	=	pEmitterParts.addLast(this);
		return this;
	}
	
	TParticle.prototype.Update = function(pCoef, pDT){
		this._Life	+=	pDT; if (this._Life>this._MaxLife){this._Sprite.destroy(); return false;}
		var l_Rate	=	parseFloat(this._Life) / this._MaxLife;
		
		this._J		=	this._VelY[0] + (this._VelY[1]-this._VelY[0])*l_Rate;;
		this._Y		+=	this._J * pCoef + this._G * pCoef;
		// clamp [and wrap]
		if (this._ClampY!=null)
			if ((2 & this._Wrap) >0){
				if (this._Y<this._ClampY[0]){
					this._Y = this._ClampY[1]-0.1;
				}else if(this._Y>this._ClampY[1])
				{
					this._Y = this._ClampY[0]+0.1;
				}
			}else{
			if ( (this._Y<this._ClampY[0]) || (this._Y>this._ClampY[1]) ) {
				this._Sprite.destroy(); return false;
			}
			
		}
		
		this._I		=	this._VelX[0] + (this._VelX[1]-this._VelX[0])*l_Rate;;
		this._X		+=	this._I * pCoef;
		if (this._ClampX!=null){
			if ((1 & this._Wrap)>0){
				if (this._X<this._ClampX[0]){
					this._X = this._ClampX[1]-0.1;
				}else if(this._X>this._ClampX[1])
				{
					this._X = this._ClampX[0]+0.1;
				}
			}else if((this._X<this._ClampX[0]) || (this._X>this._ClampX[1]) ) {
				this._Sprite.destroy(); return false;
			}
		}
		
		this._W				=	this._Width[0] + (this._Width[1]-this._Width[0])*l_Rate;
		this._H				=	this._Height[0] + (this._Height[1]-this._Height[0])*l_Rate;
		this._K				=	this._VelA[0] + (this._VelA[1]-this._VelA[0])*l_Rate;;
		this._G				=	this._Gravity[0] + (this._Gravity[1]-this._Gravity[0])*l_Rate;;
		this._Al			=	this._Alpha[0] + (this._Alpha[1]-this._Alpha[0])*l_Rate;;
		
		this._A				+=	this._K * pCoef;
		
		this._Sprite.x		=	this._X;
		this._Sprite.y		=	this._Y;
		this._Sprite.angle	=	this._A;
		this._Sprite.width	=	this._W;
		this._Sprite.height	=	this._H;
		this._Sprite.alpha	=	Clamp(this._Al,0,1);
		return true;
	}
	
	
	
	
	TEmitter.prototype.AddTemplate = function (pParticleTemplate){
		this._Templates.push(pParticleTemplate);
		this._Particles.push(new TList());
		return this._Templates.length-1;
	}
	
	TEmitter.prototype.AddSpread = function(pSpread){
		this._Spread.push(pSpread);
		return this._Spread.length-1;
	}
	
	Object.defineProperty ( TEmitter.prototype, 'count', {get:function(){
		var l_Count			=	0;
		var l_TplId			=	0;
		for (l_TplId=0; l_TplId<this._Particles.length; l_TplId++){
			l_Count			+= this._Particles[l_TplId].count();
		}
		return l_Count;
	}})
	
	TEmitter.prototype.Emit = function ( pX, pY, pTemplateId, pDirX=undefined,pDirY=undefined ) {
		var l_Tpl = this._Templates[pTemplateId];
		var l_Parts = this._Particles[pTemplateId];
		if (l_Parts.Count>=l_Tpl._MaxParticles){
			l_Parts._Sprite.destroy();
			l_Parts.removeLink(l_Parts.first());
		}
		new TParticle(l_Parts, this._Pos.x+pX,this._Pos.y+pY,l_Tpl, pDirX, pDirY);
	}
	
	TEmitter.prototype.Update = function (pSpeedFactor=1.0){
		var l_T				=	this._Time;
		this._Time			=	MilliSecs();
		var l_Dt			=	pSpeedFactor * (this._Time - l_T);
		var l_Coef			=	parseFloat(l_Dt) / 16.66667;
		
		var l_SpreadID		=	0;
		for (l_SpreadID = 0;l_SpreadID<this._Spread.length; l_SpreadID++){
			this._Spread[l_SpreadID].update(this, l_Coef, l_Dt);
		}
		
		var l_TplId			=	0;
		for (l_TplId=0; l_TplId<this._Particles.length; l_TplId++){
			var l_Parts		=	this._Particles[l_TplId];
			var l_Link		=	l_Parts.firstLink();
			while(l_Link!=null){
				var l_Part	=	l_Link.value();
				l_Link		=	l_Link.after();
				
				if (!l_Part.Update(l_Coef,l_Dt)){
					l_Parts.removeLink(l_Part._Link);
				}
			}
		}
		
	}
	
var TCloudData = function (pRGB0, pRGB1, pY0, pY1, pCountMin, pCountMax){
	
	// clouds
	var l_Layer			=	TGame.layers[TLayer.BACK];
	var l_BoundX		=	TGame.boundaries[0]-256;
	var l_BoundW		=	TGame.boundaries[2]+512;
	
	var l_cr0			=	(pRGB0 >> 16) & 0xff;
	var l_cg0			=	(pRGB0 >> 8) & 0xff;
	var l_cb0			=	pRGB0 & 0xff;
	
	var l_cr1			=	(pRGB1 >> 16) & 0xff;
	var l_cg1			=	(pRGB1 >> 8) & 0xff;
	var l_cb1			=	pRGB1 & 0xff;
	
	
	this._boundX		=	l_BoundX;
	this._boundW		=	l_BoundW;
	this._clouds		=	[];
	this._cloudsX		=	[];
	this._cloudsV		=	[];
	this._cloudsCount	=	[];
	
	// counts
	var l_nClouds		=	"";
	var l_c = 0;
	for (l_c = 0; l_c<5; l_c++){
		var l_count = Max(Floor(pCountMin+(pCountMax-pCountMin)*l_c/4), 1);
		this._cloudsCount.push(l_count);
		l_nClouds		+=	"layer["+l_c+"] n clouds = "+(this._cloudsCount[l_c])+"\n";
	}
	
	// tiles
	var l_r				=	0.1;
	for (l_c=0; l_c<this._cloudsCount[0]; l_c++){
		var l_cx		=	l_BoundX+Rand(l_BoundW);
		var l_cy		=	pY1 + (pY0-pY1) * Rnd(0.2);
		var l_cd		=	((pY1-l_cy)/(pY1-pY0))*(1-Rnd(l_r));
		SetColor			( l_cr0+(l_cr1-l_cr0)*l_cd, l_cg0+(l_cg1-l_cg0)*l_cd, l_cb0+(l_cb1-l_cb0)*l_cd);
		this._cloudsX.push	(l_cx);
		this._cloudsV.push	(Rnd(.1,0.2));
		this._clouds.push	(AddTile( 'clouds', l_cx,l_cy, 256,128, 'cloud-a-0'+Rand(1,2), l_Layer, 0 ));
	}
	
	for (l_c=0; l_c<this._cloudsCount[1]; l_c++){
		var l_cx		=	l_BoundX+Rand(l_BoundW);
		var l_cy		=	pY1 + (pY0-pY1) * Rnd(0.4);
		var l_cd		=	((pY1-l_cy)/(pY1-pY0))*(1-Rnd(l_r));
		SetColor			( l_cr0+(l_cr1-l_cr0)*l_cd, l_cg0+(l_cg1-l_cg0)*l_cd, l_cb0+(l_cb1-l_cb0)*l_cd);
		this._cloudsX.push	(l_cx);
		this._cloudsV.push	(Rnd(.1,0.3));
		this._clouds.push	(AddTile( 'clouds', l_cx,l_cy, 128,128, 'cloud-b-0'+Rand(1,2), l_Layer, 0 ));
	}
	
	for (l_c=0; l_c<this._cloudsCount[2]; l_c++){
		var l_cx		=	l_BoundX+Rand(l_BoundW);
		var l_cy		=	pY1 + (pY0-pY1) * Rnd(0.6);
		var l_cd		=	((pY1-l_cy)/(pY1-pY0))*(1-Rnd(l_r));
		SetColor			( l_cr0+(l_cr1-l_cr0)*l_cd, l_cg0+(l_cg1-l_cg0)*l_cd, l_cb0+(l_cb1-l_cb0)*l_cd);
		this._cloudsX.push	(l_cx);
		this._cloudsV.push	(Rnd(.1,0.5));
		this._clouds.push	(AddTile( 'clouds', l_cx,l_cy, 128,64, 'cloud-c-0'+Rand(1,2), l_Layer, 0 ));
	}
	
	for (l_c=0; l_c<this._cloudsCount[3]; l_c++){
		var l_cx		=	l_BoundX+Rand(l_BoundW);
		var l_cy		=	pY1 + (pY0-pY1) * Rnd(0.8);
		var l_cd		=	((pY1-l_cy)/(pY1-pY0))*(1-Rnd(l_r));
		SetColor			( l_cr0+(l_cr1-l_cr0)*l_cd, l_cg0+(l_cg1-l_cg0)*l_cd, l_cb0+(l_cb1-l_cb0)*l_cd);
		this._cloudsX.push	(l_cx);
		this._cloudsV.push	(Rnd(.1,0.8));
		this._clouds.push	(AddTile( 'clouds', l_cx,l_cy, 128,32, 'cloud-d-0'+Rand(1,2), l_Layer, 0 ));
	}
	
	for (l_c=0; l_c<this._cloudsCount[4]; l_c++){
		var l_cx		=	l_BoundX+Rand(l_BoundW);
		var l_cy		=	pY1 + (pY0-pY1) * Rnd(1);
		var l_cd		=	((pY1-l_cy)/(pY1-pY0))*(1-Rnd(l_r));
		SetColor			( l_cr0+(l_cr1-l_cr0)*l_cd, l_cg0+(l_cg1-l_cg0)*l_cd, l_cb0+(l_cb1-l_cb0)*l_cd);
		this._cloudsX.push	(l_cx);
		this._cloudsV.push	(Rnd(.1,1.0));
		this._clouds.push	(AddTile( 'clouds', l_cx,l_cy, 64,32, 'cloud-e-0'+Rand(1,4), l_Layer, 0 ));
	}
	
	// time
	this._StartTime		=	MilliSecs();
	this._Time			=	0;
	
	return this;
}

TCloudData.prototype.update = function ( pParallax = 0.0, pWind=-2.0 ) {
	
	// Update Time
	var l_OldTime		=	this._Time;
	this._Time			=	MilliSecs() - this._StartTime;
	var l_Dt			=	this._Time-l_OldTime;
	var l_Coef			=	l_Dt/16.667;
	
	// update clouds
	var l_c				=	0;
	var l_cId			=	0;
	var l_c0			=	0;
	
	for (l_cId=0; l_cId<this._cloudsCount.length; l_cId++) {
		
		for (l_c=0; l_c<this._cloudsCount[l_cId]; l_c++) {
			
			var l_i		=	l_c0+l_c;
			if (this._clouds[l_i]!=null && this._clouds[l_i]!=undefined){
				// wind and cloud speed * time coef
				var l_v		=	l_Coef * pWind * this._cloudsV[l_i];
				// size speed coef
				var l_d		=	(l_cId+1)/(this._cloudsCount[l_cId]+2);
				// invert exponential
				l_d			=	1.0 - (1.0-l_d) * (1.0-l_d);
				// update cloud pos
				this._cloudsX[l_i] += l_v * l_d;
				
				// cycle pos
				if (pWind>0)
				{
					if (this._cloudsX[l_i]>this._boundX+this._boundW+128)
					{
						this._cloudsX[l_i] -= this._boundX+this._boundW+256;
					}
				}else{
					if (this._cloudsX[l_i]<this._boundX-128)
					{
						this._cloudsX[l_i] += this._boundX+this._boundW+256;
					}
				}
				
				// update tileSprite pos (with parallax)
				this._clouds[l_i].x = this._cloudsX[l_i] + pParallax;
			}
		}
		
		l_c0 += this._cloudsCount[l_cId];
		
	}
	
}
