	TMenu.FilterEvents = function (pContext, pEvent) {
		return pContext.filter(pEvent);
	}
	TMenu.prototype.setPage = function (pPageId){
		if (pPageId==this._CurPage){return;}
		// hide previous page
		if (this._CurPage>=0) { this._Body[this._CurPage].visible = false; }
		// set new page
		this._CurPage = pPageId;
		// show new page
		if (this._CurPage>=0) { this._Body[this._CurPage].visible = true; }
	}
	TMenu.prototype.filter = function (pEvent){
		var l_CurPage = this._CurPage;
		switch(pEvent.source){
			case this._BtPrev[1]:
				if (pEvent.id==EVENT_GADGETACTION){this.setPage(0);}
				return null;
			case this._BtPrev[2]:
				if (pEvent.id==EVENT_GADGETACTION){this.setPage(1);}
				return null;
			case this._BtNext[0]:
				if (pEvent.id==EVENT_GADGETACTION){this.setPage(1);}
				return null;
			case this._BtNext[1]:
				if (pEvent.id==EVENT_GADGETACTION){this.setPage(2);}
				return null;
			case this._BtExit[0]:
			case this._BtExit[1]:
			case this._BtExit[2]:
				if (pEvent.id==EVENT_GADGETACTION){DisableMenu();}
				return null;
		}
		
		// not an event we filtered -> returns it, so it will be caught by the main loop.
		return pEvent;
	}
	TMenu.prototype.volumeStr = function (pVol){
		l_Vol = parseInt(pVol); return (l_Vol<10 ? "0"+l_Vol : l_Vol);
	}
	TMenu.prototype.updateVolumes = function () {
		this._Vol.text		=	"Volume  <"+this.volumeStr(TGame.MASTER_VOLUME*TMenu.VOLUME_SCALE)+">";
		this._VolZik.text	=	"Musique <"+this.volumeStr(TGame.MUSIC_VOLUME*TMenu.VOLUME_SCALE)+">";
		this._VolFx.text	=	"Effets  <"+this.volumeStr(TGame.FX_VOLUME*TMenu.VOLUME_SCALE)+">";
		this._VolAmb.text	=	"Ambiance<"+this.volumeStr(TGame.AMBIENT_VOLUME*TMenu.VOLUME_SCALE)+">";
	}
	
	TMenu.prototype.enable = function() {
		this._Main.visible	=	true;
	}
	
	TMenu.prototype.disable = function() {
		this._Main.visible	=	false;
	}
	
	TMenu.prototype.update = function(pCoef) {
		switch(this._State){
			// enable menu (fade in)
			case 0:
				this.enable();
				this._Main.visible = true;
				this._Main.alpha += pCoef*0.1;
				if (this._Main.alpha>=1){
					this._Main.alpha = 1;
					this._State = 1;
				}
				this._Context._Session.pause();
				var l_Chars = TCharacter.List.firstLink();
				while (l_Chars!=null){
					l_Chars.value().pause();
					l_Chars = l_Chars.after();
				}
				break;
				
			// update menu here
			case 1:
				if (KeyHit(KEY_ESCAPE)){
					this._State = 2;
					return;
				}
				break;
				
			// disable menu (fade out)
			case 2:
				this._Main.alpha -= pCoef*0.1;
				// quit menu
				if (this._Main.alpha<=0){
					this._Main.alpha = 0;
					this._State = 0;
					this.disable();
					this._Context.SetMode(this._Context._PrevMode);
				}
				var l_Chars = TCharacter.List.firstLink();
				while (l_Chars!=null){
					l_Chars.value().resume();
					l_Chars = l_Chars.after();
				}
				this._Context._Session.resume();
				break;
		}
		
		FlushKeys();
	};


var TCentral = function (pX,pY, pLayerId, pStep=0) {
	if (TCentral._List==undefined){ TCentral._List = new TList(); }
	TCentral._List.addLast(this);
	
	var l_States		=	Push2D();
	
	this._LayerID		=	pLayerId;
	this._Pos			=	new Vector2(pX,pY);
	this._Period		=	Rand(360);
	this._Frequence		=	Rnd(0.5,1.5);
	this._Step			=	pStep;
	this._Time			=	0;
	
	SetHandle				( 0.5, 0 );
	this.Sprite			=	AddTile ( 'tiles', pX, pY, 256, 256, 'central', TGame.layers[pLayerId], 3 );
	SetOrigin(0,0);
	SetHandle				( 0.0, 0 );
	this.CrackUp		=	AddTile	( 'tiles', 0	, 32-6	, 32	, 96	, 'central-crack-1-0'	, this.Sprite, 3 );
	this.CrackDown		=	AddTile	( 'tiles', 0	, 128+19, 96	, 96	, 'central-crack-2-0'	, this.Sprite, 3 );
	
	SetColor				( 255,255,255 );
	SetAlpha				( 0.0 );
	SetHandle				( 0.5, 0 );
	this.Glow			=	AddTile	( 'tiles', 0	, 0		, 256	, 256	, 'central-glow'		, this.Sprite, 3 );
	SetAlpha				( 1 );
	
	this.enable			=	(pStep>0);
	
	switch(this._Step){
		case 0:break;
		case 1:break;
		case 2:break;
			this.CrackUp.frameName = 'central-crack-1-0';
			this.CrackDown.frameName = 'central-crack-2-0';
			break;
		case 3:
			this.CrackUp.frameName = 'central-crack-1-1';
			this.CrackDown.frameName = 'central-crack-2-1';
			break;
		case 4:
			this.CrackUp.frameName = 'central-crack-1-2';
			this.CrackDown.frameName = 'central-crack-2-2';
			break;
		case 5:
		case 6:
		case 7:
			this.CrackUp.frameName = 'central-crack-1-3';
			this.CrackDown.frameName = 'central-crack-2-3';
			break;
	}
	
	Pop2D					( l_States );
	return this;
}
