TEolien.Render = function (pCoef) {
	if (TEolien._List==undefined)return false;
	
	var l_DT= pCoef * 1000 / (__LOOP_FPS__);
	
	var l_Lnk = TEolien._List.firstLink();
	while (l_Lnk!=null){
		var l_C = l_Lnk.value();
		switch (l_C._Step){
			// disabled
			case 0:
				break;
				
			// Activating
			case 1:
				l_C._Time = 0;
				l_C._Step++;
				break;
				
			// increases frequency
			case 2:
				l_C._Time += l_DT;
				if (l_C._Time>5000){ l_C._Time=0; l_C._Step++;}
				else{
					l_C._CurFrequency = (l_C._Frequency) * (l_C._Time/5000);
					l_C.Pales.angle	+=	l_C._CurFrequency*360*l_DT;
				}
				break;
				
			// rotates.
			case 3:
				l_C.Pales.angle	+=	l_C._Frequency*360*l_DT;
				break;
				
			// disables
			case 4:
				l_C._Time = 0;
				l_C._Step++;
				break;
				
			// lower frequency
			case 5:
				l_C._Time += l_DT;
				if (l_C._Time>5000){ l_C._Time=0; l_C._Step = 0; l_C._CurFrequency = 0; }
				else{
					l_Freq = (l_C._CurFrequency) * ( 1.0 - l_C._Time/5000);
					l_C.Pales.angle	+=	l_Freq*360*l_DT;
				}
				break;
		}
		l_Lnk = l_Lnk.after();
	}
}

TEolien.inRange = function(pX,pLayerID, pRange){
	if (TEolien._List==undefined)return false;
	
	var l_Rng		=	pRange*pRange;
	var l_Px		=	ConvertParallaxX(pX, pLayerID);
	var l_Lnk		=	TEolien._List.firstLink();
	while (l_Lnk!=null){
		var l_C		=	l_Lnk.value();
		var l_Cx	=	ConvertParallaxX(l_C._Pos.x, l_C._LayerID);
		if (Abs((l_Px-l_Cx)*(l_Px-l_Cx))<=l_Rng){return true;}
		l_Lnk = l_Lnk.after();
	}
	return false;
}
