				if (this.jsonCharacters===undefined)	{throw ("missing mandatory node 'characters'")};
				this.jsonSounds		=	this.jsonAssets.sounds;
				if (this.jsonSounds===undefined)	{throw ("missing mandatory node 'sounds'")};
