	callOptions: function() {
		if (TGame.MENU==3){return;}
		
		for (i in TGame.MENU._MenuElements){
			TGame.MENU._MenuElements[i].visible = false;
		}
		for (i in TGame.MENU._OptionsElements){
			TGame.MENU._OptionsElements[i].visible = true;
		}
		TGame.MENU._State = 3;
	},
	
	callMenu: function() {
		if (TGame.MENU==0){return;}
		
		this._State = 0;
		
		this._TStats.hide();
		this._TStats.reset();
		
		
		for (i in this._MenuElements){
			this._MenuElements[i].visible = true;
		}
		for (i in TGame.MENU._OptionsElements){
			TGame.MENU._OptionsElements[i].visible = false;
		}
		for (i in TGame.MENU._CreditsElements){
			TGame.MENU._CreditsElements[i].visible = false;
		}
		for (i in TGame.MENU._MenuElements){
			TGame.MENU._MenuElements[i].visible =
				(TGame.MENU._MenuElements[i].__ButtonEnabled==undefined?
				true:
				TGame.MENU._MenuElements[i].__ButtonEnabled);
		}
		
		// restore intro (if required)
		if (this._Channel_Intro==null){
			if (this._Current_Channel!=null) {
				this._Current_Channel.destroy();
			}
			this._Current_Channel = PlayMusic ( 'music_intro', true, TGame.MASTER_VOLUME*TGame.MUSIC_VOLUME, true );
			this._Channel_Intro = this._Current_Channel;
		}
		
		if (this._Current_Channel != this._Channel_Intro){
			PauseChannel(this._Current_Channel);
			ResumeChannel(this._Channel_Intro);
			this._Current_Channel = this._Channel_Intro;
		}
		
		this._Credits_Scroll		=	0;
	},
	
