	update: function(){
		/* ------------------------------------------------------- */
		/* - Initialize loop -									   */
		/* ------------------------------------------------------- */
			// Update Time
			var l_OldTime		=	this._Time;
			this._Time			=	MilliSecs() - this._StartTime;
			var l_Dt			=	this._Time-l_OldTime;
			var l_Coef			=	l_Dt/16.667;
			
			this._Rays1.angle	=	 MilliSecs()*0.022;
			this._Rays2.angle	=	-MilliSecs()*0.027;
			
			// switch music on/off
			if (KeyHit(KEY_M)){
				TGame.MUSIC_VOLUME = this._BaseMusicVolume-TGame.MUSIC_VOLUME;
				TSound.UpdateMusicVolumes(TGame.MASTER_VOLUME*TGame.MUSIC_VOLUME);
				console.log("mute music");
			}
			
			// update
				// TAnimShape.Update();
				TGame.emitters.Update(1);
				TScreenFilter.Update();
				
//total money request
		if (this._totalMoneyTrigger == true && this._totalMoneyReq.response != ""){
			this._totalMoney = parseInt(this._totalMoneyReq.response);
			this._totalMoneyTrigger = false;
			this._TStats.setEuros(this._totalMoney);
			this._TStats._eurosText.text = numberWithSpace(this._totalMoney)+'€';
			this._TStats.refresh();
		}
		
		// gauge
		switch (this._State ) {
			case 1 :
				this._TStats.Update();
				break;
			case 2 :
				this._CreditStartTime		+=	l_Dt * (KeyDown(KEY_LSHIFT) ? 4 : 1);
				this._Credits_Back.alpha	=	Clamp((this._CreditStartTime)/2000, 0,1);
				this._CreditLayer.y			=	TGame.height + 50 - this._CreditStartTime/50;
				break;
		}
	},
	
	
