var TGauge = function(stats, statsElement, pos, bar, group, title, title2, subTitle, flag = 0){
	this._TStats = stats;
	this._State = 0;
	this._euros = 0;
	this._eurosGoal = 0;
	this._pValue = this._euros/this._eurosGoal;
	this._pDelay = 10;
	
	SetStrokeColor					( 0,0,0 );
	
	
	SetColor						( 0,0,0 );
	SetHandle						( 0.5, 0.5 );
	this._BarRect	=	AddRect	(  pos.x-bar.w/2, pos.y, bar.w, bar.h, group, flag );
	this._BarRect.visible = false;
	statsElement.push(this._BarRect);
	
	SetColor						( 255,255,100 );
	this._BarRect0	=	AddRect	( this._BarRect.x+2, this._BarRect.y+2, bar.w-4, bar.h-4, group, flag );
	this._BarRect0.visible = false;
	statsElement.push(this._BarRect0);
	
	SetColor						( 10,143,254 );
	this._BarRect1	=	AddRect	( this._BarRect.x+2, this._BarRect.y+2, bar.w-4, bar.h-4, group, flag );
	this._BarRect1.visible = false;
	this._BarRect1.scale.setTo(0,1);
	statsElement.push(this._BarRect1);

	SetFontSize						( 16 );
	SetColor						( 255,255,255 );
	SetHandle						( 1, 0.5 );
	
	this._procentText				=	AddText	( "",
									  this._BarRect.x + bar.w-5,
									  this._BarRect.y+bar.h/2+4,
									  group, flag );
	this._procentText.alpha = 0;
	this._procentText.visible = false;
	statsElement.push(this._procentText);
	
	SetHandle						( 0.5, 0 );
	this._barTitle				=	AddText	( title,
									  this._BarRect.x+this._BarRect.width/2,
									  this._BarRect.y+bar.h+2,
									  group, flag );
	this._barTitle.visible = false;
	statsElement.push(this._barTitle);
	
	SetFontSize						( 10 );
	SetHandle						( 0.5, 0 );
	this._barTitle2				=	AddText	( title2,
									  this._BarRect.x+this._BarRect.width/2,
									  this._barTitle.y+20,
									  group, flag );
	this._barTitle2.visible = false;
	statsElement.push(this._barTitle2);
	
	SetHandle						( 0.5, 0 );
	SetFontSize						( 10 );
	this._barSubTitle				=	AddText	( subTitle,
									  this._BarRect.x+this._BarRect.width/2,
									  this._BarRect.y-14,
									  group, flag );
	this._barSubTitle.visible = false;
	statsElement.push(this._barSubTitle);
	
	SetHandle						( 0, 0.5 );
}


TGauge.prototype.Update = function () {
	switch (this._State){
	case 0:
		req = Interpolation(this._BarRect1.scale.x, this._pValue);
		this._BarRect1.scale.x = req[0];
		check = req[1];
		if (check == true){
			this._State = 1;
		}
		break;
	
	case 1:
		this._TStats._eurosText.alpha = Interpolation(this._TStats._eurosText.alpha, 1)[0];
		req = Interpolation(this._procentText.alpha, 1);
		this._procentText.alpha = req[0];
		check = req[1];
		if (check == true){
			this._State = 2;
		}
		break;
	}
}

TGauge.prototype.setEuros = function(euros, goal, name = null, text = null){
	this._euros = euros;
	this._eurosGoal = goal;
	this._pValue = this._euros/this._eurosGoal;
	this._procentText.text = Math.round(this._pValue*100,0)+"%";
	
	console.log(name, text);
	
	if (name != null){
		this._barTitle.text = name;
	}
	if (text != null){
		this._barTitle2.text = text;
	}
}

TGauge.prototype.reset = function(){
	this._State = 0;
	this._procentText.alpha = 0;
	this._BarRect1.scale.x = 0;
}

