TStats.prototype.setEuros = function(euros){
	
	var i = -1;
	var sum = -0.1;
	while (sum < euros){
		i++;
		sum += this._subsidiaryGoals[i].cost;
	}
	
	eurosFirstBar = euros;
	if (i>0){
		sum = 0;
		for (j=0; j<i; j++){
			sum +=this._subsidiaryGoals[j].cost
		}
		eurosFirstBar = euros-sum;
	}
	console.log(eurosFirstBar)
	
	this._firstBar.setEuros(eurosFirstBar, this._subsidiaryGoals[i].cost, this._subsidiaryGoals[i].name, this._subsidiaryGoals[i].text);
	this._secondBar.setEuros(euros, 273e9);
}

