var Run_Prez = function ( pAction, pParams ) {
	
	var l_Name		=	pParams[0];
	if (this._Step==undefined){
		this._Step = 0;
	}else{
		this._Step = 1;
		if (pAction.rate==1){
			this._Step=2;
		}
	}
	
	switch ( this._Step ) {
		
		case 0: // init (then update)
			
			this._TimeScale			=	pAction.length / 4050;	// the prez is set fo 4050 ms
																// if the action use a different timing
																// we'll use a factor to calibrate the prez
																// so it ends synchronously
			this._Name				=	l_Name;
			this._Shapes			=	new TList();
			this._Length			=	pAction.length;
			this._CharID			=	TGame.CharacterIDByName ( l_Name );
			
			this._Layer				=	TGame.game.add.group();
			this._LayerLabels		=	TGame.game.add.group();
			
			var l_Len				=	this._Length;
			
			var l_W					=	TGame.width;
			var l_H					=	TGame.height;
			var l_Hw				=	TGame.halfWidth;
			var l_Hh				=	TGame.halfHeight;
			var l_Hg				=	TGame.groundYMin;
			var l_Shape				=	null;
			
			var l_Color1			=	TGame.characters_color0[this._CharID];
			var l_Color2			=	TGame.characters_color1[this._CharID];
			
			// back manga
				SetAlpha				( 1.0 );
				SetHandle				( 0.0, 0.0 );
				SetHexColor				( l_Color1 );
				this._BackColor		=	AddRect( 0, 0,l_W, l_H, this._Layer, 4 );
				
			// rays - manga -
				SetHandle				( 0.5, 0.5 );
				SetHexColor				( l_Color2 );
				SetScale				( 1.8, 1.8 );
				this._Back			=	AddImage		( 'prez-back', l_W*2/5,l_H*3/5, this._Layer, 7 );
				this._Back1			=	AddImage		( 'prez-back', l_W*2/5,l_H*3/5, this._Layer, 7 );
				SetScale				( 1.0, 1.0 );
				
			// Avatar
				SetAlpha				( 1 );
				SetColor				( 255, 255, 255 );
				SetHandle				( 0.5, 0.5 );
				SetScale				( -1, 1 );
				this._Avatar		=	AddTile	(	'bars',
													l_W*2/3,
													l_Hh,
													192,192,
													'avatar-64-'+l_Name,
													this._LayerLabels,
													7
												);
				this._Avatar.angle	=	5;
				
			// Name
				SetScale				( 1,1 );
				SetFontSize				( 50 );
				SetColor				( 255,255,100 );
				SetStrokeSize			( 4 );
				SetStrokeColor			( 000,000,000 );
				this._CharName		=	AddText	(	TGame.characters_pseudo[this._CharID],
													l_Hw*0.7,
													l_Hh*3/5,
													this._LayerLabels,
													4
												);
				this._CharName.angle=	-15;
				
			// Title
				SetColor				( 255, 255, 255 );
				this._CharTitle		=	AddTile (	'prez-stuff',
													0,0,
													AtlasWidth('prez-stuff','quote-'+l_Name)*2,
													AtlasHeight('prez-stuff','quote-'+l_Name)*2,
													'quote-'+l_Name,
													this._LayerLabels, 7
												);
				this._CharTitle.pivot.setTo(0,0);
				this._CharTitle.angle=	-8;
				
		// enable the main layer
		l_Shape						=	new TAnimShape (this._Layer, true);
			this._Shapes.addLast		( l_Shape );
			l_Shape.addAnimator			( 'enable'		,    1,    1 );
			l_Shape.addAnimator			( 'alpha'		,    1,  150, 0, 1 );
			l_Shape.addAnimator			( 'play-music'	,   50,    0, 'poum', 1.5 );
			l_Shape.addAnimator			( 'play-music'	,  550,    0, 'repoum', 1.5 );
			l_Shape.addAnimator			( 'play-music'	, 1050,    0, 'swoosh', 1.0 );
			l_Shape.addAnimator			( 'alpha'		, 3750,  150, 1, 0 );
			l_Shape.addAnimator			( 'disable'		, 4000,    1 );
			
		l_Shape						=	new TAnimShape (this._LayerLabels, true);
			this._Shapes.addLast		( l_Shape );
			l_Shape.addAnimator			( 'enable'		,    1,    1 );
			l_Shape.addAnimator			( 'alpha'		,  100,   50, 0, 1 );
			l_Shape.addAnimator			( 'alpha'		, 3750,  150, 1, 0 );
			l_Shape.addAnimator			( 'disable'		, 4000,    1 );
			
		// animate the manga background
		l_Shape						=	new TAnimShape (this._BackColor , false); this._Shapes.addLast(l_Shape);
			l_Shape.addAnimator			( 'enable'		,   50,  1 );
			l_Shape.addAnimator			( 'disable'		, 4000,  1 );
		l_Shape						=	new TAnimShape (this._Back , false); this._Shapes.addLast(l_Shape);
			l_Shape.addAnimator			( 'enable'		,   50,    1 );
			l_Shape.addAnimator			( 'angle'		,   50, 3950, 120,200 );
			l_Shape.addAnimator			( 'disable'		, 4000,    1 );
		l_Shape						=	new TAnimShape (this._Back1, false); this._Shapes.addLast(l_Shape);
			l_Shape.addAnimator			( 'enable'		,   50,    1 );
			l_Shape.addAnimator			( 'angle'		,   50, 3950, 0,-170 );
			l_Shape.addAnimator			( 'disable'		, 4000,     1 );
			

		// show the avatar
		l_Shape						=	new TAnimShape (this._Avatar , false); this._Shapes.addLast(l_Shape);
			l_Shape.addAnimator			( 'enable'	, l_Len-4000, 1 );
			//l_Shape.addAnimator		( 'angle'	, l_Len-3510, 3500, 5, -5 );
			l_Shape.addAnimator			( 'position', l_Len-3510, 3500, l_W*2/3, l_Hh, l_W*2/3-15, l_Hh+5 );
			l_Shape.addAnimator			( 'disable'	, l_Len-  50, 1 );
			
		// Name
		l_Shape						=	new TAnimShape (this._CharName , false); this._Shapes.addLast(l_Shape);
			l_Shape.addAnimator			( 'enable'	, l_Len-3500,   1 );
			l_Shape.addAnimator			( 'angle'	, l_Len-3510, 3500, -15, -5 );
			l_Shape.addAnimator			( 'disable'	, l_Len-  50,   1 );
			
		// Title-Quote
		var l_Shape					=	new TAnimShape (this._CharTitle , false); this._Shapes.addLast(l_Shape);
			l_Shape.addAnimator			( 'enable'	,  1050,   1 );
			l_Shape.addAnimator			( 'position',  1050, 300,
														-l_Hw,
														l_H * 1.25,
														l_Hw * 0.75,
														l_H * 0.8
										);
			l_Shape.addAnimator			( 'position',  1350, 2500,
														l_Hw * 0.75,
														l_H * 0.8,
														l_Hw * 0.80,
														l_H * 0.75
										);
			l_Shape.addAnimator			( 'angle'	, 1350, 2650, -8, 3 );
			l_Shape.addAnimator			( 'disable'	, 4000,   1 );
			
			SetHandle					( 0,0 );
			SetFontSize					( 12 );
			
			var l_Link				=	this._Shapes.firstLink();
			while (l_Link!=null){
				var l_Shape			=	l_Link.value();
				l_Link				=	l_Link.after();
				l_Shape.animate			( true );
				l_Shape.scaleTime		( this._TimeScale );
			}
			TBands169.Start(1, 1500, true);
			
		case 1: // update
			
			// update animators
			var l_T					=	pAction.rate*pAction.length;
			var l_Link				=	this._Shapes.firstLink();
			while (l_Link!=null)
			{
				var l_Shape			=	l_Link.value();
				l_Link				=	l_Link.after();
				l_Shape.updateTime		( l_T );
			}
			TGame.game.world.bringToTop	( this._Layer );
			TBands169.BringToTop		( );
			TGame.game.world.bringToTop	( this._LayerLabels );
			break;
			
		case 2: // release
			TBands169.Start(0, 1500, true);
			break;
	}
	
}


var LoadCinematicSmith = function () {
	var l_DialogRects		=	[[40,90, 480,80, 150], [TGame.width-520,90, 480,80, 250]];
	var l_Motion	=	new TMotion ( 'smith' );
		l_Motion		.setDialog	( l_DialogRects[0], l_DialogRects[1] );
		
		// screen fader
		l_Motion		.addAction	( 'screen-fader',    0,    1, null, 1, true );
		
		// sand screen
		l_Motion		.addAction	( 'set-color'		,	 0,    1, null, 220, 150, 080 );
		l_Motion		.addAction	( 'set-alpha'		,	 0,    1, null, 0.5 );
		l_Motion		.addAction	( 'addrect'			,    0,    1, '[screen-sand]',-20,-20, TGame.width+40, TGame.height+40, TLayer.POSTFX, 4 );
		l_Motion		.addAction	( 'def-shape-alpha'	,	 1, 1000, null, '[screen-sand]', 0, 0.4 );
		
		l_Motion		.addAction	( 'play-music'	,    0,    1, null, TGame.Musics[0][0], 1, true, '[channel:0]' );
		l_Motion		.addAction	( 'screen-fader',  500, 2000, null, 0, true );
		l_Motion		.addAction	( 'screen-bands',  500,  250, null, 1, true );
		
		/*l_Motion		.addDialog	( 1, "Vous arrivez trop tard...", 2000, 600, 'any', true );
		l_Motion		.addDialog	( 1, "Nous avons pris le contrôle des États,\n"+
										 "et de bientôt tous les internets !\n"+
										 "Rentrez chez vous...", -1, 100 );
		l_Motion		.addDialog	( 0, "C'est ce qu'on va voir ballot !\n"+
										 "Can't Stenchon the Mélenchon !", -1, 100 );*/
		l_Motion		.addDialog	( 1, "You came too late...", 2000, 600, 'any', true );
		l_Motion		.addDialog	( 1, "We already took control of states\n"+
										 "and soon, the internet ! !\n"+
										 "Begone !", -1, 100 );
		l_Motion		.addDialog	( 0, "You'll pay for your misdeeds !\n"+
										 "Can't Stenchon the Mélenchon !", -1, 100 );
		
		
		l_Motion		.addAction	( 'swap-channel',   -1,    1, null, '[channel:0]', '[channel:1]' );
		l_Motion		.addAction	( 'music-volume',   -1,  250, null, '[channel:1]', 1, 0 );
		l_Motion		.addAction	( 'free-channel',   -1,    1, null, '[channel:1]' );
		l_Motion		.addAction	( 'play-music'	,   -1,    1, null, TGame.Musics[0][1], 1, true, '[channel:0]' );
		l_Motion		.addAction	( 'screen-filter',  -1,    1, null, 255,180,170, 0.6, 1 );
		l_Motion		.addAction	( 'screen-fader-stop',-1,  1, null );
		l_Motion		.addAction	( 'screen-bands',   -1,  250, null, 0, true );
		l_Motion		.addAction	( 'callback'	,    0, 3500, null, function (pAction)
		{
			if (this._Step==undefined){
				this._Step = 0;
			}else{
				this._Step = 1;
			}
			var l_Player = pAction.motion.valueForKey("[player]");
			var l_Char = pAction.motion.valueForKey("[char]");
			l_Player.unlock				( );
			l_Player._Cinematic		=	true;
			l_Player._Dir.x			=	1;
			l_Player._Anim			=	'attente';
			l_Player._Pos.x			=	TGame.width;
			
			l_Char.unlock();
			l_Char._Cinematic		=	true;
			l_Char._Anim			=	'attente';
			l_Char._Dir.x			=	-1;
			l_Char._Dirty			=	true;
			
			if (this._Step==0){
				this._CamX0			=	2000;//TGame.boundaries[0]+TGame.boundaries[2]*.7 - TGame.width;
				this._CamX1			=	(l_Player._Pos.x + l_Char._Pos.x)/2;
				FlushMouseHit();
				FlushKeyHit();
				FlushTouches();
			}else{
				// update camera
				var l_CamX			=	this._CamX1;
				if (pAction.motion.sequencer.paused==false){
					// position camera for cinematic -> focus at center.
					if (pAction.time<2000){
						l_CamX		=	this._CamX0 + (this._CamX1 - this._CamX0) * (pAction.time/2000);
					}
				}
				TGame.game.camera.focusOnXY ( l_CamX, TGame.halfHeight );
			}
			TCharacter.Render();
		}, true );
		
		// disable sand screen
		l_Motion		.addAction	( 'def-shape-alpha'	,	 -1,  250, null, '[screen-sand]', 0.4, 0 );
		
		// camera focus
		MOTION_EVAL 					( [l_Motion		,    -2,  250],	['camera-focus',
																			['vec', ['vec-mid', ['get-char-pos','[player]'], ['get-char-pos', '[char]'] ],
																				['get-char-pos','[player]']
																			]
																		] );
		l_Motion		.addAction	( 'destroy'			,	 -1,    1, null, '[screen-sand]' );
	return l_Motion;
}

