var Motion_ReplaceX = function (pPlayer, pSrc, pDst){
	
	var l_Y					=	pPlayer.y;
	
	var l_Motion			=	new TMotion();
	var l_Len				=	Abs(pSrc-pDst)*4;
	l_Motion.addCharacter		( '[player]'		, pPlayer );
	
	l_Motion.addAction			( 'def-char-dir-x'	,    0,     1, null, '[player]', (pDst<pSrc?-1:1) );
	l_Motion.addAction			( 'char-play'		,    0, l_Len, null, '[player]', 'marche', 8 );
	l_Motion.addAction			( 'def-vec'			,    0,     1, '[src]', pSrc, l_Y  );
	l_Motion.addAction			( 'def-vec'			,    0,     1, '[dst]', pDst, l_Y  );
	l_Motion.addAction			( 'char-render'		,    0, l_Len, null );
	l_Motion.addAction			( 'vec'				,    0, l_Len, '[cur]', '[src]', '[dst]' );
	l_Motion.addAction			( "set-char-pos"	,    0, l_Len, null, '[player]', '[cur]' );
	l_Motion.addAction			( 'camera-focus'	,    0, l_Len, null, '[cur]', true );
	l_Motion.addAction			( 'char-play'		,l_Len,     1, null, '[player]', 'attente', 1, false );
	
	return l_Motion;
}

