		updateTile:function (pTile, pLayerID){
			if ((1 & pTile._FX) > 0){return;}
			
			var l_L	=	pLayerID;
			var l_C	=	1 - TGame.layers[l_L]._DEPTH / TGame.layers[TLayer.BACK]._DEPTH;
			var l_R = LerpFloat( this._COEF_R, 1, l_C );
			var l_G = LerpFloat( this._COEF_G, 1, l_C );
			var l_B = LerpFloat( this._COEF_B, 1, l_C );
			if (pTile._ColorR==undefined){
				var l_Tint = pTile.tint;
				pTile._ColorR = (l_Tint >> 16) & 0xff;
				pTile._ColorG = (l_Tint >> 8) & 0xff;
				pTile._ColorB = l_Tint & 0xff;
				pTile._ColorC = 1;
			}
			
			SetShapeColor(	pTile, 
							LerpInt ( pTile._ColorR, pTile._ColorR * l_R, pTile._ColorC ),
							LerpInt ( pTile._ColorG, pTile._ColorG * l_G, pTile._ColorC ),
							LerpInt ( pTile._ColorB, pTile._ColorB * l_B, pTile._ColorC ) );

		},
		
		updateTiles:function (){
			
			var l_L = 0;
			for (l_L = 0;l_L<=TLayer.FRONT2; l_L++){
				var l_Children = TGame.layers[l_L].children;
				
				var l_C	=	1 - TGame.layers[l_L]._DEPTH / TGame.layers[TLayer.BACK]._DEPTH;
				var l_R = LerpFloat( this._COEF_R, 1, l_C );
				var l_G = LerpFloat( this._COEF_G, 1, l_C );
				var l_B = LerpFloat( this._COEF_B, 1, l_C );
				
				this._updateTileSub(l_Children, l_R,l_G,l_B);
			}
		},
		
		_updateTileSub:function(pChildren, pR, pG, pB){
			var l_ChildID=0;
			for (l_ChildID=0; l_ChildID<pChildren.length; l_ChildID++){
				var l_Child = pChildren[l_ChildID];
				if (l_Child._FX==undefined || ((1 & l_Child._FX)==0)){
					if (l_Child._ColorR==undefined){
						var l_Tint = l_Child.tint;
						l_Child._ColorR = (l_Tint >> 16) & 0xff;
						l_Child._ColorG = (l_Tint >> 8) & 0xff;
						l_Child._ColorB = l_Tint & 0xff;
						l_Child._ColorC = 1;
					}
					
					SetShapeColor(	l_Child, 
									LerpInt ( l_Child._ColorR, l_Child._ColorR * pR, l_Child._ColorC ),
									LerpInt ( l_Child._ColorG, l_Child._ColorG * pG, l_Child._ColorC ),
									LerpInt ( l_Child._ColorB, l_Child._ColorB * pB, l_Child._ColorC ) );
					this._updateTileSub(l_Child.children, pR,pG,pB);
				}
			}		
		},
	
