	UpdateGame:function(pCoef) {
		
		if (MilliSecs()>this._SessionTimer){
			this._SessionTimer = MilliSecs()+SESSION_PING_DELAY;
			this._Session.ping();
		}
		
		/* ------------------------------------------------------- */
		/* - Logic -											   */
		/* ------------------------------------------------------- */
			
			if (KeyHit(KEY_M) && GAME_MODE == MODE_GOD){
				this.SetMode(this.MODE_SETUP);
				return;
			}
			
			// IA Spawner
				
				if (this._Player._State != STATE_DEAD) {
					var l_Spawner	=	this._Spawners[this._SpawnerID];
					l_Spawner			.update(pCoef*1000/__LOOP_FPS__,
												this.game.camera.x);
				}
				
				if (KeyHit(KEY_K) && GAME_MODE == MODE_GOD){
					if (this._Player.level>0){
						this._Player.unlock();
						if (this._Player._Target!=null){
							this._Player._Target = null;
						}
						this._Player.wait();
						this._Player.lock();
						var l_Lnk = TCharacterIA.List.firstLink();
						while (l_Lnk!=null){
							var l_IA = l_Lnk.value();
							l_Lnk = l_Lnk.after();
							l_IA.remove();
						}
						this._Spawners[this._SpawnerID].stop();
					}
				}
				
				if (KeyHit(KEY_E) && GAME_MODE == MODE_GOD) {this._Spawners[this._SpawnerID].spawn(this.game.camera.x);}
				if (KeyHit(KEY_U) && GAME_MODE == MODE_GOD) {this.SetMode ( this.MODE_OVER );}
				
			// updates all characters
				
				// if level 0 - Tutorial
				if (this._Player.level==0){
					
					var l_DR = KeyDown(KEY_RIGHT);
					var l_KR = KeyHit(KEY_RIGHT);
					var l_KL = KeyHit(KEY_LEFT);
					var l_KU = KeyHit(KEY_UP);
					var l_KS = KeyHit(KEY_SPACE);
					
					// disable all inputs
					FlushKeys		( );
					FlushMouse		( );
					FlushTouches	( );
					
					this._Tutorial.visible	=	true;
					
					switch(this._ModeStep){
						case 0:
							
							this._Tutorial.text = "Get closer to the enemy";
							// move the player to a catch position
							var l_Target = this._Player.getNearestIAX(0,true);
							if(l_Target!=null){
								if (this._Player.x>l_Target.x-TGame.targetTolerance*.95){
									this._Player.unlock();
									this._Player.x = l_Target.x-TGame.targetTolerance*.95;
									this._Player.lock();
									this._ModeStep++;
									break;
								}
								if (this._Player.distance(l_Target)<=TGame.targetTolerance*.95){
									this._ModeStep++;
									break;
								}
								this._Player.unlock();
								this._Player.moveRight ( pCoef );
								this._Player.lock();
							}
							break;
							
						case 1:
							this._Tutorial.text = "To catch him\nRun over him [ ⇨ ]";
							if (this._Player._State==STATE_TAKE){
								this._ModeStep++;
								break;
							}
							// move right only !
							SetKeyState(KEY_RIGHT, l_KR);
							SetKeyDownState(KEY_RIGHT, l_DR);
							break;
							
						case 2:
							this._Tutorial.text = "To shake him up and get money\nPress Up\n[ ⇧ ]";
							if ((this._Player._Target.hasMoney()==false)){
								this._ModeStep++;
								break;
							}
							// move right only !
							SetKeyState(KEY_UP, l_KU);
							break;
							
						case 3:
							//this._Tutorial.text = "Puis ... jetez le ... \n[ ⇦ | ⇨ ]";
							this._Tutorial.text = "Then ... Throw him away ! \n[ ⇦ | ⇨ ]";
							if ((this._Player._Target==null) || (this._Player._Target._State==STATE_THROWN)){
								this._ModeStep++;
								this._Tutorial.visible	=	false;
								break;
							}
							// throw only !
							SetKeyState(KEY_LEFT, l_KL);
							SetKeyState(KEY_RIGHT, l_KR);
							break;
							
						default:
							// just wait, it should pass the first level.
					}
				}else{
					if (this._Tutorial!=null){
						this._Tutorial.visible=false;
						this._Tutorial.destroy();
						this._Tutorial = null;
					}
				}
				
				if (this._Player._PickedItem!=null){
					if (this._Player._PickedItem._Style==ITEM.SPEED){
						pCoef	*=	this._Player._PickedItem._Value;
					}
				}
				
				var l_Coef = pCoef;
				// prevent update to move characters too fast on each iteration
				if (l_Coef>1.5){
					
					LockMouseHit();
					LockKeyHit();
					while (l_Coef>2.0){
						TCharacter.Update(this, 2);
						l_Coef -= 2.0;
					}
					TCharacter.Update(this, l_Coef);
					UnlockMouseHit();
					UnlockKeyHit();
					
				}else{
					
					TCharacter.Update(this, l_Coef);
					
				}
				
			// separates IA characters
				
				// this._Player.avoidCollisions(TCharacterIA.List, 72);
				
				
			// separates IA characters
				
				TItem.Update(this, this._Player, pCoef);
				
			// update camera
				
				TGame.game.camera.focusOnXY			( this._Player.x, TGame.halfHeight );
				
				
			// update parallax
				
				this._ParallaxX					=	this.game.camera.x;
				
				
		/* ------------------------------------------------------- */
		/* - UI -												   */
		/* ------------------------------------------------------- */
			
			// update avatars
				
				this._AvatarPlayer.Update(this._Player);
				
			// update character's bar (if a target is held, taken or shaked)
				
				var l_PrevAvatarID = this._CurAvatar;
				this._CurAvatar = -1;
				
				if (this._Player._Target!=null) {
					switch (this._Player._Target._State){
						case STATE_HELD:
						case STATE_TAKEN:
						case STATE_SHAKED:
							var l_C = 0;
							for (l_C=0; l_C<this._Avatars.length; l_C++){
								if (this._Player._Target._Name.toLowerCase()==this._Avatars[l_C]._Name.toLowerCase())
								{
									this._CurAvatar	=	l_C;
									break;
								}
							}
					}
				}
				if (l_PrevAvatarID!=this._CurAvatar) {
					if (l_PrevAvatarID >=0){this._Avatars[l_PrevAvatarID ].visible = false; }
					if (this._CurAvatar>=0){this._Avatars[this._CurAvatar].visible = true; }
				}
				if (this._CurAvatar>=0){ this._Avatars[this._CurAvatar].Update(this._Player._Target); }
				
			// update sky
				// clouds
			//	this.clouds.update(this._ParallaxX*0.9, -5);
				
			// update particles
				TGame._Emitter				.Update();
				
			// update score
				this._Score.text		=	numberWithSpace( this._Player._Money )+' €';
				this._Score.x			=	TGame.halfWidth;
				this._Score.anchor.x	=	0.5;
				
			//update combo
				this.UpdateCombo(pCoef);
				
			// update layers depth
				// sort characters from time to time to restore the z-depth order
				if(this._LayerTime+TCharacter._DelaySortGroup <= this._Time){
					this._LayerTime	+=	TCharacter._DelaySortGroup;
					// asked to update faster the first group (player)
					TGame.layers[TLayer.PLAYER].sort('y', Phaser.Group.SORT_ASCENDING);
					// reset to normal delay
					TCharacter._DelaySortGroup = TCharacter._DelaySortGroupSlow;
				}
		
	},
	
	
