//$target = sessions || euros || throws || shakes
function getTotal($target)
{
    return $.ajax({
        url: $apiUrl+"/total/"+$target,
        type: "GET",
        success: function(result) {
            //alert(result);
            return parseInt(result);
        }
    });
}


function startSession()
{
    return $.ajax({
        url: $apiUrl + "/session",
        type: "POST",
        data: {},
        dataType: "json",
        success: function(data) {
            return data.key;
        }
    });
}

function endSession($key, $level, $euros, $shakes, $throws)
{
    return $.ajax({
        url: $apiUrl + "/session/" + $key,
        type: "POST",
        data: {
            _method: "PUT",
            level: $level,
            euros: $euros,
            shakes: $shakes,
            throws : $throws
        },
        dataType: "json",
        success: function(data) {
            return data.message;
        }
    });
}

